from kivy.app import Appfrom kivy.uix.widget import Widgetfrom kivy.core.window import Windowfrom kivy.lang import BuilderBuilder.load_file('./DumCulator.kv')Window.size = (450, 350)class DumCulatorWidget(Widget):    def button_value(self, number):        prev_number = self.ids.input_box.text        if prev_number == '0':            self.ids.input_box.text = ''            self.ids.input_box.text = f'{number}'        else:            self.ids.input_box.text = f'{prev_number}{number}'    def get_sign(self, sign):        prev_number = self.ids.input_box.text        self.ids.input_box.text = f"{prev_number}{sign}"class DumCulatorApp(App):    def build(self):        return DumCulatorWidget()if __name__ == '__main__':    DumCulatorApp().run()